#WINDOW Creation(Root Window)from tkinter import *import pymysqlroot=Tk()root.title("Welcome Page")root.geometry('1366x768')root.configure(background='grey')def insert():    db = pymysql.connect("localhost","root","admin","MYDATABASE")    cursor = db.cursor()    sqlinsert2 = "INSERT INTO BIRTHDAY (ID, NAME, DATE) VALUES (1,'minnujimmy','2019/11/21')"    cursor.execute(sqlinsert2)    db.commit()    db.closedef select():    db = pymysql.connect("localhost","root","admin","MYDATABASE")    cursor = db.cursor()    sqldate = "SELECT NAME FROM BIRTHDAY WHERE DATE = CURDATE()"    cursor.execute(sqldate)    row = cursor.fetchall()    if row:        for raw in row:            print(raw)    db.commit()    db.close    def delete():    db = pymysql.connect("localhost","root","admin","MYDATABASE")    cursor = db.cursor()    sqldelete = "TRUNCATE TABLE BIRTHDAY"    cursor.execute(sqldelete)def update():    db = pymysql.connect("localhost","root","admin","MYDATABASE")    cursor = db.cursor()    sqlinsert2 = "UPDATE BIRTHDAY SET DATE = '2019/11/22' WHERE NAME = 'minnujimmy'"    cursor.execute(sqlinsert2)    sqldate = "SELECT DATE FROM BIRTHDAY WHERE NAME = 'minnujimmy'"    cursor.execute(sqldate)    row = cursor.fetchall()    if row:        for raw in row:            print(raw)    db.commit()    db.closeLabel(root, text="PLEASE CLICK BELOW OPTIONS", bg="blue",width="200", height="2").grid(row=0, columnspan = 3, rowspan = 2)b = Button(root, text="Insert", command = insert)b.grid(row=2,column=0)b2 = Button(root, text="Select", command = select)b2.grid(row=2,column=1)b3 = Button(root, text="Delete", command = delete)b3.grid(row=4,column=0)b4 = Button(root, text="Update", command = update)b4.grid(row=4,column=1)root.mainloop()